value.value:
{
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "0076D7",
    "summary": "Pipeline status alert message​​​​",
    "sections": [
        {
            "activityTitle": "Pipeline execution alert​​​​",
            "facts": [
                {
                    "name": "Subscription Id:",
                    "value": "@{pipeline().parameters.subscription}"
                },
                {
                    "name": "Resource Group:",
                    "value": "@{pipeline().parameters.resourceGroup}"
                },
                {
                    "name": "Data Factory Name:",
                    "value": "@{pipeline().DataFactory}"
                },
                {
                    "name": "Pipeline RunId:",
                    "value": "@{pipeline().parameters.runId}"
                },
                {
                    "name": "Pipeline Name:",
                    "value": "@{pipeline().parameters.name}"
                },
                {
                    "name": "Pipeline Status:",
                    "value": "@{pipeline().parameters.status}"
                },
                {
                    "name": "Execution Start Time (SGT):",
                    "value": "@{convertTimeZone(pipeline().parameters.triggerTime, 'UTC', 'Singapore Standard Time')}"
                },
                {
                    "name": "Message:",
                    "value": "@{replace(replace(replace(replace(replace(replace(pipeline().parameters.supportMessage,'"',''),'\n','~~'),'\',''),'~~','\n'),'{',''),'}','')}"
                },
                {
                    "name": "Notification Time (SGT):",
                    "value": "@{convertTimeZone(utcnow(), 'UTC', 'Singapore Standard Time')}"
                }
            ],
            "markdown": true
        }
    ],
    "potentialAction": [
        {
            "@type": "OpenUri",
            "name": "View pipeline run",
            "targets": [
                {
                    "os": "default",
                    "uri": "@{concat('https://adf.azure.com/monitoring/pipelineruns/',pipeline().parameters.runId,'?factory=%2Fsubscriptions%2F',pipeline().parameters.subscription,'%2FresourceGroups%2F',pipeline().parameters.resourceGroup,'%2Fproviders%2FMicrosoft.DataFactory%2Ffactories%2F',pipeline().DataFactory)}"
                }
            ]
        }
    ]
}
